import lombok.extern.slf4j.Slf4j;
import org.junit.Test;
import org.springframework.util.Base64Utils;

import java.nio.charset.StandardCharsets;
@Slf4j
public class Base64Test {

    @Test
    public void encode() throws Exception{
        String privateKey="";
        String privateKeyStr=Base64Utils.encodeToString(privateKey.getBytes(StandardCharsets.UTF_8));
        log.info("private key after encode {}",privateKeyStr);

        String publicKey="";

        String publicKeyStr=Base64Utils.encodeToString(publicKey.getBytes(StandardCharsets.UTF_8));
        log.info("publicKey key after encode {}",publicKeyStr);
    }
}
